sys_ux_data_broker_transform.script:
//0a52d3dcd7011200f2d224837e6103f2 user group sys
function transform(input){
	var myArr = [];
	var grMem = new GlideRecord('sys_user_grmember');
	grMem.addQuery('group','0a52d3dcd7011200f2d224837e6103f2');
	grMem.query();

	while(grMem.next()){
		var members = {};
		var email = new GlideRecord('sys_user');
		email.get(grMem.getValue('user'));

		members.sublabel = email.getValue('email');
		members.id = grMem.getValue('user').toString();
		members.label = grMem.getDisplayValue('user');
		myArr.push(members);
	}

	return myArr;
}
